import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { Chart } from "chart.js";
import { getCountryData, getCountryHistory } from './GetAPI';

const CountryDetails = () => {
    const {countryName} = useParams();
    const [countryData, setCountryData] = useState(null);
    const [countryHistoricalData, setCountryHistoricalData] = useState(null);

    useEffect(() => {
        const fetchCountryData = async () => {
            try {
                const data = await getCountryData();
                const selectedCoutry = data.find(country => country.country === countryName);

                setCountryData(selectedCoutry);
            } catch (error) {
                console.error('Error fetching country data:', error);
            }
        };

        fetchCountryData();
    }, [countryName]);

    useEffect(() => {
        const fetchCountryHistoricalData = async () => {
            try {
                const data = await getCountryHistory(countryName);
                setCountryHistoricalData(data.timeline);
            } catch (error) {
                console.error('Error fetching historical data:', error);
            }
        };

        fetchCountryHistoricalData();
    }, [countryName]);


    useEffect(() => {
        let chart = null;

        if(countryHistoricalData) {
            const dates = Object.keys(countryHistoricalData.cases);
            const casesData = Object.keys(countryHistoricalData.cases);
            const recoveredData = Object.keys(countryHistoricalData.recovered);
            const deathsData = Object.keys(countryHistoricalData.deaths);

            const ctx = document.getElementById('myChart2');

            if(ctx) {
                if (chart) {
                    chart.destroy();
                  }
            chart = new Chart(ctx, {
                type: 'line',
                data: {
                    labels: dates,
                    datasets: [
                        {
                            label: 'Cases',
                            data: casesData,
                            borderColor: 'blue',
                            borderWidth: 1,
                            fill: false,
                        },
                        {
                            label: 'Recovered',
                            data: recoveredData,
                            borderColor: 'green',
                            borderWidth: 1,
                            fill: false,
                        },
                        {
                            label: 'Deaths',
                            data: deathsData,
                            borderColor: 'red',
                            borderWidth: 1,
                            fill: false,
                        },
                    ],
                },
                options: {
                   
                },
            });
        }
    }

        return () => {
            if(chart) {
                chart.destroy();
            }
        

    };
    }, [countryHistoricalData]);
    

    if (!countryData) {
        return <div>Loading...</div>
    }

    const {country, countryInfo, population, continent, cases, recovered, deaths} = countryData;

    return (
        <div>
            <img src= {countryInfo?.flag} alt={`${country} Flag`} />
            <br></br>
            <br></br>
            <h1>{country}</h1>
            <p>Continent: {continent}</p>
            <p>Population: {population}</p>
            <div>
                <br></br>
                <br></br>
                <h2>COVID-19 Stats</h2>
                <p>Cases: {cases}</p>
                <p>Recovered: {recovered || (recovered === 0 ? "Data not available" : recovered)}</p>
                <p>Deaths: {deaths}</p>
            </div>
            <br></br>
            <br></br>
            <div> <Link to="/">BACK</Link></div>
            <div>
                <h1>{countryData.country} Statistics</h1>
                <canvas id="myChart2" width="400" height="200"></canvas>
            </div>
        </div>
    );
};

export default CountryDetails;
