import React, {useState} from "react";
import { BrowserRouter as Router, Routes, Route} from "react-router-dom";
import Sidebar from "./Sidebar";
import Graph from "./Graph";
import WorldMap from "./WorldMap";
import CountryDetails from "./CountryDetails";
import './styles.css';
import getAPI from "./getAPI";{}

const App = () => {


    {/*const { countries, totalCases, sortedCountriesData } = getAPI(); */}

    const [activeTab, setActiveTab] = useState('cases');

    const handleTabChange = (tab) => {
      setActiveTab(tab);
    }

    return (
      <Router>
        <div className="app">
          <Sidebar onTabChange={handleTabChange} />
          <div className="content">
            <Routes>
              <Route path="/" element={<HomePage activeTab={activeTab} />} />
              <Route path="/countries/:countryName" element={<CountryDetails />} />
            </Routes>
          </div>
        </div>
      </Router>
    );
  };

  const HomePage = ({ activeTab }) => {
    return (
      <>
        <WorldMap activeTab={activeTab} />
        <Graph activeTab={activeTab} />
      </>
    );
  };

  export default App;
