import { useEffect, useState } from "react"

const useGetAPI = () => {

    const [countries, setCountries] = useState([]);
    const [totalData, setTotalData] = useState(null);
    const [sortedCountryData, setSortedCountryData] = useState([]);


    useEffect(() => {
        const fetchData = async () => {
          try {
            const totalResponse = await fetch('https://disease.sh/v3/covid-19/all');
            const totalData = await totalResponse.json();
            setTotalData(totalData);
    
            const countriesResponse = await fetch('https://disease.sh/v3/covid-19/countries');
            const countriesData = await countriesResponse.json();
            const sortedData = countriesData.sort((a, b) => b[activeTab] - a[activeTab]);
            setSortedCountryData(sortedData);
          } catch (error) {
            console.error('Error fetching data:', error);
          }
        };
    
        fetchData();
      }, [activeTab]);

      return (countries, totalData, sortedCountryData);
    
}

export default getAPI;
