import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { Chart } from "chart.js";
import { getCountryData } from './GetAPI';

const CountryDetails = () => {
    const {countryName} = useParams();
    const [countryData, setCountryData] = useState(null);

    useEffect(() => {
        const fetchCountryData = async () => {
            try {
                const data = await getCountryData();
                const selectedCoutry = data.find(country => country.country === countryName);

                setCountryData(selectedCoutry);
            } catch (error) {
                console.error('Error fetching country data:', error);
            }
        };

        fetchCountryData();
    }, [countryName]);


    useEffect(() => {
        if(countryData) {
            const ctx = document.getElementById('myChart2').getContext('2d');
            new Chart(ctx, {
                type: 'line',
                data: {
                    labels: ['Cases', 'Recovered', 'Deaths'],
                    datasets: [
                        {
                            label: countryData.country,
                            data: [countryData.cases, countryData.recovered, countryData.deaths],
                            borderColor: ['blue', 'green', 'black'],
                            borderWidth: 1,
                            fill: false,
                        },
                    ],
                },
                options: {
                    scales: {
                        y: {
                            beginAtZero: true,
                        },
                    },
                },
            });
        }
    }, [countryData]);
    

    if (!countryData) {
        return <div>Loading...</div>
    }

    const {country, countryInfo, population, continent, cases, recovered, deaths} = countryData;

    return (
        <div>
            <img src= {countryInfo?.flag} alt={`${country} Flag`} />
            <br></br>
            <br></br>
            <h1>{country}</h1>
            <p>Continent: {continent}</p>
            <p>Population: {population}</p>
            <div>
                <br></br>
                <br></br>
                <h2>COVID-19 Stats</h2>
                <p>Cases: {cases}</p>
                <p>Recovered: {recovered || (recovered === 0 ? "Data not available" : recovered)}</p>
                <p>Deaths: {deaths}</p>
            </div>
            <br></br>
            <br></br>
            <div> <Link to="/">BACK</Link></div>
            <div>
                <h1>{countryData.country} Statistics</h1>
                <canvas id="myChart" width="400" height="200"></canvas>
            </div>
        </div>
    );
};

export default CountryDetails;
