            <h1>{country.country}</h1>
            <p>Continent: {country.continent}</p>
            <p>Population: {country.population}</p>
            <div>
                <h2>COVID-19 Stats</h2>
                <p>Cases: {country.cases}</p>
                <p>Recovered: {country.recovered}</p>
                <p>Deaths: {country.deaths}</p>
            </div>
        </div>
    );
